mapper.select:
SELECT `zhstu`.*, `szjf`.`id` AS `szjfId`, `szjf`.`she_hui_fu_wu` AS `shfw`, `szjf`.`she_hui_shi_jian` AS `shsj`, `szjf`.`bi_sai_huo_jiang` AS `bshj`, `szjf`.`xue_sheng_gan_bu` AS `xsgb`, GET_SZJYJFPF(`szjf`.`she_hui_fu_wu`+ `szjf`.`she_hui_shi_jian`+ `szjf`.`bi_sai_huo_jiang`+ `szjf`.`xue_sheng_gan_bu`) AS `szjfScore`, `szjf`.`is_valid` AS `szjfState` FROM (SELECT `stu`.`id` AS `stuId`, `stu`.`sno` AS `sno`, `stu`.`name` AS `name`, `zh`.`term_id` AS `termId`, `zh`.`id` AS `zhId`, `zh`.`is_valid` AS `zhState` FROM `t_zhcpcjtj` `zh` LEFT JOIN `student` `stu` ON `zh`.`sno` = `stu`.`sno` WHERE
`zh`.`term_id` = #{termId}
AND `zh`.`id` = #{zhId}
AND `stu`.`class_id` = #{classId}
) `zhstu` LEFT JOIN `t_szjyjfpf` `szjf` ON `zhstu`.`zhId` = `szjf`.`zong_he_id` ORDER BY
`szjfScore` DESC, `sno` ASC
`sno`
