`zh`.`term_id` = #{termId}
AND `zh`.`id` = #{zhId}
AND `stu`.`class_id` = #{classId}
) `zhstu` LEFT JOIN `t_szxfrcxwbfpf` `rcxw` ON `zhstu`.`zhId` = `rcxw`.`zong_he_id` ORDER BY
`rcxwScore` DESC, `sno` ASC
