mapper.select:
SELECT `zsr`.*, `szjf`.`id` AS `szjfId`, `szjf`.`zong_he_id`+ `szjf`.`she_hui_fu_wu`+ `szjf`.`she_hui_shi_jian`+ `szjf`.`bi_sai_huo_jiang`+ `szjf`.`xue_sheng_gan_bu` AS `szjfScore`, `szjf`.`is_valid` AS `szjfState`
FROM (SELECT `zhstu`.*, `rcxw`.`id` AS `rcxwId`, `rcxw`.`she_hui_gong_de`+ `rcxw`.`wen_ming_jiao_wang`+ `rcxw`.`cheng_xin_li_shen`+ `rcxw`.`ti_yu_duan_lian`+ `rcxw`.`ai_hu_gong_wu`+ `rcxw`.`xue_xiao_gui_ding`+ `rcxw`.`can_jia_huo_dong`+ `rcxw`.`ting_ke_ji_lu`+ `rcxw`.`gong_yu_jian_cha` AS `rcxwScore`, `rcxw`.`is_valid` AS `rcxwState` FROM (SELECT `stu`.`id` AS `stuId`, `stu`.`sno` AS `sno`, `stu`.`name` AS `name`, `zh`.`id` AS `id`, `zh`.`term_id` AS `termId`, `zh`.`id` AS `zhId`, `zh`.`ping_jun_xue_fen_ji_dian` AS `xfjd`, `zh`.`is_valid` AS `zhState` FROM `t_zhcpcjtj` `zh` LEFT JOIN `student` `stu` ON `zh`.`sno` = `stu`.`sno` WHERE
`zh`.`id` = #{zhId}
AND `zh`.`term_id` = #{termId}
) `zhstu` LEFT JOIN `t_szxfrcxwbfpf` `rcxw` ON `zhstu`.`id` = `rcxw`.`zong_he_id` ) `zsr` LEFT JOIN `t_szjyjfpf` `szjf` ON `zsr`.`id` = `szjf`.`zong_he_id` ORDER BY `sno`
