mapper.select:
SELECT `t`.`id` AS `termId`, `t`.`name` AS `name`, DATE_FORMAT(`t`.`start_date`, '%Y-%m-%d') AS `startDate`, DATE_FORMAT(`t`.`stop_date`, '%Y-%m-%d') AS `stopDate`, `t`.`teacher_id` AS `teacherId`, `teacher`.`name` AS `teacherName` FROM (SELECT * FROM `term` WHERE `term`.`is_valid` = 'Y'
AND `term`.`teacher_id` = #{teacherId}
) `t` LEFT JOIN `teacher` ON `t`.`teacher_id` = `teacher`.`id`
WHERE `t`.`name` LIKE '%${search}%' OR `teacher`.`name` LIKE '%${search}%' OR `t`.`start_date` LIKE '%${search}%' OR `t`.`stop_date` LIKE '%${search}%'
ORDER BY `t`.`stop_date`, `t`.`start_date`,`t`.`id`
